nta.declaration:
hybrid clock s_payoff = 0;
hybrid clock r_payoff = 0;

broadcast chan help_victim, contact_first_responder;

hybrid clock t;

const int TAU = 50;
hybrid clock evac_time = TAU;

template.declaration:
meta int dist = 0;

void set(double r, double s, bool update_time) {
    r_payoff = r;
    s_payoff = s;
    if (update_time) evac_time = t;
    else evac_time = 0;
}

nta.system:
r = Robot();

// Rationality Degree: 1.0 -> Perfectly Rational Agent, 0.0 -> Perfectly Irrational Agent
s = Survivor(1.0, 6, 50, 50);

system r,s;
